import React,{useState} from "react";
import Button from '../Ui/Button/Button.js'

const FormSection = (props) =>{
    const [inputData, setInputData] = useState({
        name:'',
        age:''
    });
    const [nameValide, setNameValide] = useState(true);
    const [ageValide, setAgeValide] = useState(true);

    const nameChangeHandler = (e) =>{
        setNameValide(true);
        setInputData(prevData => {
            return {
                ...prevData,
                name:e.target.value
            }
        })
    }

    const ageChangeHandler = (e) =>{
        setAgeValide(true);
        setInputData((prevData) =>{
            return {
                ...prevData,
                age: e.target.value
            }
        })
    }

    const formSubmitHandler = (e)=>{
        e.preventDefault();
        let result = inputData;
        result['id'] = Math.random().toString()
        // console.log( result);
        if(inputData.name.trim().length <= 0 ){
            setNameValide(false);
            
        } 
        if(inputData.age.trim().length <= 0 ){
            setAgeValide(false);
       
        }
        if(inputData.name.trim().length > 0 && inputData.age.trim().length > 0){
            props.onSetListInput(result);
            setInputData({
                name:'',
                age:''
            });
        }
        
    }

    return (
        <>
        <div className='form-section'>
            <div className='card'>
                <div className='card-body'>
                    <form onSubmit={formSubmitHandler}>
                    <div className="form-group">
                        <label >Name</label>
                        <input type="text" value={inputData.name} onChange={nameChangeHandler} className="form-control"/>
                    
                    </div>
                    <div className="form-group">
                        <label >age</label>
                        <input type="number" value={inputData.age} onChange={ageChangeHandler} className="form-control" />
                    </div>
                    
                    <Button />
                    </form>
                </div>
            </div>
        </div>
        </>
    );
}

export default FormSection;
