import React from "react";
import classes from './Input.module.css'

const Input = (props) =>{

    return ( <div
        className={`${classes.control} ${
            props.isValide === false ? classes.invalid : ''
        }`}
      >
        <label htmlFor="email">{props.id}</label>
        <input
          type={props.type}
          id={props.id}
          value={props.value}
          onChange={props.onChange}
          onBlur={props.onBlur}
        />
      </div>);
}

export default Input;
